import tkinterdef Press1():    label_1.configure(text = "BEGIN")    def Press2():    label_1.configure(text = "OPTIONS")def Press3():    label_1.configure(text = "NOTHING HERE")def Press4():    label_1.configure(text = "GOODBYE")#Window Settingsmarc = tkinter.Tk()marc.title("Arrow")marc.geometry('300x300')marc.configure(bg="black")#Window Settingslabel_1 = tkinter.Label(marc, text="Title")label_1.pack()but1 = tkinter.Button(marc, text="Start", command=Press1, width=10)but1.pack()but2 = tkinter.Button(marc, text="Options", command=Press2, width=10)but2.pack()but3 = tkinter.Button(marc, text="Cheats", command=Press3, width=10)but3.pack()but4 = tkinter.Button(marc, text="Exit", command=Press4, width=10)but4.pack()                         marc.mainloop()